import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Reporting() {
  const [lowStock, setLowStock] = useState([]);
  const [sales, setSales] = useState([]);
  const [products, setProducts] = useState([]);
  const [totalItemsBought, setTotalItemsBought] = useState(0);
  const [totalRevenue, setTotalRevenue] = useState(0);
  const [remainingWorth, setRemainingWorth] = useState(0);
  const [refreshKey, setRefreshKey] = useState(0);

  const refreshData = () => {
    setRefreshKey(prev => prev + 1);
  };

  useEffect(() => {
    Promise.all([
      axios.get('/reports/lowstock'),
      axios.get('/sales'),
      axios.get('/products')
    ])
      .then(([lowRes, salesRes, prodRes]) => {
        setLowStock(lowRes.data);
        setSales(salesRes.data);
        setProducts(prodRes.data);

        const itemsBought = salesRes.data.reduce((sum, sale) => {
          const quantity = parseInt(sale.quantity) || 0;
          console.log(`Sale ID ${sale.id}: Quantity ${quantity}, Running Sum ${sum + quantity}`);
          return sum + quantity;
        }, 0);
        setTotalItemsBought(itemsBought);

        const revenue = salesRes.data.reduce((sum, sale) => {
          const product = prodRes.data.find(p => p.id === parseInt(sale.productId));
          if (!product) {
            console.warn(`Product not found for sale ID ${sale.id}, productId ${sale.productId}`);
            return sum;
          }
          if (!product.price || product.price <= 0) {
            console.warn(`Invalid price for product ID ${sale.productId}: ${product.price}`);
            return sum;
          }
          const saleRevenue = product.price * sale.quantity;
          console.log(`Sale ${sale.id}: Product ${sale.productId}, Qty ${sale.quantity}, Price ${product.price}, Revenue ${saleRevenue}`);
          return sum + saleRevenue;
        }, 0);
        setTotalRevenue(revenue);

        const worth = prodRes.data.reduce((sum, p) => {
          if (!p.price || p.price <= 0) {
            console.warn(`Invalid price for product ID ${p.id}: ${p.price}`);
            return sum;
          }
          return sum + (p.quantity * p.price);
        }, 0);
        setRemainingWorth(worth);
      })
      .catch(err => console.error('Error fetching data:', err));
  }, [refreshKey]);

  return (
    <div className="content">
      <h2>Reporting</h2>
      
      
      <h3>Low Stock Products</h3>
      <table>
        <thead><tr><th>Name</th><th>Description</th><th>Quantity</th><th>Price</th></tr></thead>
        <tbody>
          {lowStock.map(p => (
            <tr key={p.id}>
              <td>{p.name}</td>
              <td>{p.description}</td>
              <td>{p.quantity}</td>
              <td>${p.price?.toFixed(2) || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Sales Records Summary</h3>
      <table>
        <tbody>
          <tr><td>Total Items Bought</td><td>{totalItemsBought}</td></tr>
          <tr><td>Total Money Recovered from Sales</td><td>${totalRevenue.toFixed(2) || '0.00'}</td></tr>
          <tr><td>Total Worth of Remaining Products</td><td>${remainingWorth.toFixed(2) || '0.00'}</td></tr>
        </tbody>
      </table>

      <h3>Sales History</h3>
      <table>
        <thead><tr><th>Product ID</th><th>Quantity</th><th>Date</th></tr></thead>
        <tbody>
          {sales.map(s => (
            <tr key={s.id}>
              <td>{s.productId}</td>
              <td>{s.quantity}</td>
              <td>{s.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Reporting;
