import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Sales() {
  const [products, setProducts] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [form, setForm] = useState({ productId: '', customerId: '', quantity: 0 });
  const [error, setError] = useState('');

  useEffect(() => {
    Promise.all([axios.get('/products'), axios.get('/customers')])
      .then(([prodRes, custRes]) => {
        setProducts(prodRes.data);
        setCustomers(custRes.data);
      })
      .catch(err => console.error(err));
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setError(''); // Clear error on input change
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('/sales', form)
      .then(() => {
        alert('Sale recorded successfully');
        setForm({ productId: '', customerId: '', quantity: 0 });
        setError('');
      })
      .catch(err => {
        setError(err.response?.data?.error || 'An error occurred');
      });
  };

  return (
    <div>
      <h2>Sales</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <select name="productId" value={form.productId} onChange={handleChange} required>
          <option value="">Select Product</option>
          {products.map(p => (
            <option key={p.id} value={p.id}>
              {p.name} (Stock: {p.quantity}, Price: {p.price})
            </option>
          ))}
        </select>
        <select name="customerId" value={form.customerId} onChange={handleChange} required>
          <option value="">Select Customer</option>
          {customers.map(c => (
            <option key={c.id} value={c.id}>{c.name}</option>
          ))}
        </select>
        <input
          name="quantity"
          type="number"
          value={form.quantity}
          onChange={handleChange}
          placeholder="Quantity"
          min="1"
          required
        />
        <button type="submit">Record Sale</button>
      </form>
    </div>
  );
}

export default Sales;
