import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [products, setProducts] = useState([]);
  const [editProductId, setEditProductId] = useState(null);

  useEffect(() => {
    axios.get('/products')
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleImageChange = (e, productId) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        axios.put(`/products/${productId}`, { image: reader.result })
          .then(res => {
            setProducts(products.map(p => p.id === productId ? res.data : p));
            setEditProductId(null);
          })
          .catch(err => console.error(err));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="content">
      <h2>Dashboard</h2>
      <div className="card-container">
        {products.map(product => (
          <div key={product.id} className="card">
            {product.image && <img src={product.image} alt={product.name} />}
            <h3>{product.name}</h3>
            <p>Quantity: {product.quantity}</p>
            <p>Price: ${product.price?.toFixed(2) || 'N/A'}</p>
            <div className="card-edit">
              <input
                type="file"
                accept="image/*"
                onChange={(e) => handleImageChange(e, product.id)}
              />
              <button onClick={() => setEditProductId(null)}>Cancel</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
