import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Sales() {
  const [products, setProducts] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [form, setForm] = useState({ productId: '', customerId: '', quantity: 0 });

  useEffect(() => {
    Promise.all([axios.get('/products'), axios.get('/customers')])
      .then(([prodRes, custRes]) => {
        setProducts(prodRes.data);
        setCustomers(custRes.data);
      });
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('/sales', form).then(() => {
      alert('Sale recorded');
      setForm({ productId: '', customerId: '', quantity: 0 });
    }).catch(err => alert(err.response.data.error));
  };

  return (
    <div>
      <h2>Record Sale</h2>
      <form onSubmit={handleSubmit}>
        <select name="productId" value={form.productId} onChange={handleChange} required>
          <option value="">Select Product</option>
          {products.map(p => <option key={p.id} value={p.id}>{p.name} (Stock: {p.quantity})</option>)}
        </select>
        <select name="customerId" value={form.customerId} onChange={handleChange} required>
          <option value="">Select Customer</option>
          {customers.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
        </select>
        <input name="quantity" type="number" value={form.quantity} onChange={handleChange} placeholder="Quantity" required />
        <button type="submit">Record Sale</button>
      </form>
    </div>
  );
}

export default Sales;
