import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Reporting() {
  const [lowStock, setLowStock] = useState([]);
  const [sales, setSales] = useState([]);

  useEffect(() => {
    Promise.all([axios.get('/reports/lowstock'), axios.get('/sales')])
      .then(([lowRes, salesRes]) => {
        setLowStock(lowRes.data);
        setSales(salesRes.data);
      });
  }, []);

  return (
    <div>
      <h2>Reporting</h2>
      <h3>Low Stock Products</h3>
      <table>
        <thead><tr><th>Name</th><th>Quantity</th></tr></thead>
        <tbody>{lowStock.map(p => <tr key={p.id}><td>{p.name}</td><td>{p.quantity}</td></tr>)}</tbody>
      </table>
      <h3>Sales History</h3>
      <table>
        <thead><tr><th>Product ID</th><th>Customer ID</th><th>Quantity</th><th>Date</th></tr></thead>
        <tbody>{sales.map(s => <tr key={s.id}><td>{s.productId}</td><td>{s.customerId}</td><td>{s.quantity}</td><td>{s.date}</td></tr>)}</tbody>
      </table>
    </div>
  );
}

export default Reporting;
