import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Sales() {
  const [products, setProducts] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get('/products')
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleCheckboxChange = (productId) => {
    setSelectedItems(prev => {
      if (prev.some(item => item.productId === productId)) {
        return prev.filter(item => item.productId !== productId);
      } else {
        return [...prev, { productId, quantity: 1 }];
      }
    });
  };

  const handleQuantityChange = (productId, value) => {
    setSelectedItems(prev =>
      prev.map(item =>
        item.productId === productId ? { ...item, quantity: parseInt(value) || 1 } : item
      )
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedItems.length === 0) {
      setError('Please select at least one product');
      return;
    }
    axios.post('/sales', selectedItems)
      .then(() => {
        alert('Sale recorded successfully');
        setSelectedItems([]);
        setError('');
        axios.get('/products').then(res => setProducts(res.data)); // Refresh products
      })
      .catch(err => {
        setError(err.response?.data?.error || 'An error occurred');
      });
  };

  const calculateTotal = () => {
    return selectedItems.reduce((sum, item) => {
      const product = products.find(p => p.id === parseInt(item.productId));
      return sum + (product?.price * (parseInt(item.quantity) || 0) || 0);
    }, 0);
  };

  return (
    <div className="content">
      <h2>Sales</h2>
      {error && <p style={{ color: '#000000' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="product-list">
          {products.map(product => (
            <div key={product.id} className="product-list-item">
              <input
                type="checkbox"
                checked={selectedItems.some(item => item.productId === product.id)}
                onChange={() => handleCheckboxChange(product.id)}
              />
              <span>{product.name} (Stock: {product.quantity}, Price: ${product.price?.toFixed(2)})</span>
              <input
                type="number"
                min="1"
                value={selectedItems.find(item => item.productId === product.id)?.quantity || 1}
                onChange={(e) => handleQuantityChange(product.id, e.target.value)}
                disabled={!selectedItems.some(item => item.productId === product.id)}
              />
            </div>
          ))}
        </div>
        <button type="submit">Record Sale</button>
      </form>
      {selectedItems.length > 0 && (
        <div className="receipt">
          <h3>Receipt</h3>
          {selectedItems.map((item, index) => {
            const product = products.find(p => p.id === parseInt(item.productId));
            return (
              <div key={index} className="receipt-item">
                {product?.name}: {item.quantity} x M{product?.price?.toFixed(2)} = M{(product?.price * item.quantity || 0).toFixed(2)}
              </div>
            );
          })}
          <div className="receipt-item"><strong>Total: M{calculateTotal().toFixed(2)}</strong></div>
        </div>
      )}
    </div>
  );
}

export default Sales;
