import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [stats, setStats] = useState({ products: 0, lowStock: 0, sales: 0 });

  useEffect(() => {
    Promise.all([
      axios.get('/products'),
      axios.get('/reports/lowstock'),
      axios.get('/sales')
    ]).then(([productsRes, lowRes, salesRes]) => {
      setStats({
        products: productsRes.data.length,
        lowStock: lowRes.data.length,
        sales: salesRes.data.length
      });
    });
  }, []);

  return (
    <div>
      <h2>Dashboard</h2>
      <p>Total Products: {stats.products}</p>
      <p>Low Stock Items: {stats.lowStock}</p>
      <p>Total Sales: {stats.sales}</p>
    </div>
  );
}

export default Dashboard;
