import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Sales() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ productId: '', quantity: 0 });

  useEffect(() => {
    axios.get('/products').then(res => setProducts(res.data));
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('/sales', form).then(() => {
      alert('Sale recorded');
      setForm({ productId: '', quantity: 0 });
    }).catch(err => alert(err.response.data.error));
  };

  return (
    <div>
      <h2>Sales</h2>
      <form onSubmit={handleSubmit}>
        <select name="productId" value={form.productId} onChange={handleChange} required>
          <option value="">Select Product</option>
          {products.map(p => <option key={p.id} value={p.id}>{p.name} (Stock: {p.quantity})</option>)}
        </select>
        <input name="quantity" type="number" value={form.quantity} onChange={handleChange} placeholder="Quantity" required />
        <button type="submit">Record Sale</button>
      </form>
    </div>
  );
}

export default Sales;
