import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Inventory() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', category: '', price: 0, quantity: 0 });
  const [editId, setEditId] = useState(null);
  const [addQty, setAddQty] = useState({});

  useEffect(() => {
    axios.get('/products').then(res => setProducts(res.data));
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editId) {
      axios.put(`/products/${editId}`, form).then(res => {
        setProducts(products.map(p => p.id === editId ? res.data : p));
        setEditId(null);
      });
    } else {
      axios.post('/products', form).then(res => {
        setProducts([...products, res.data]);
      });
    }
    setForm({ name: '', description: '', category: '', price: 0, quantity: 0 });
  };

  const handleDelete = (id) => {
    axios.delete(`/products/${id}`).then(() => {
      setProducts(products.filter(p => p.id !== id));
    });
  };

  const handleEdit = (product) => {
    setForm(product);
    setEditId(product.id);
  };

  const handleQtyChange = (id, value) => {
    setAddQty({ ...addQty, [id]: value });
  };

  const handleAddStock = (id) => {
    const qty = parseInt(addQty[id] || 0);
    if (qty > 0) {
      axios.post('/stock/add', { productId: id, quantity: qty }).then(() => {
        setProducts(products.map(p => p.id === id ? { ...p, quantity: p.quantity + qty } : p));
        setAddQty({ ...addQty, [id]: 0 });
      });
    }
  };

  return (
    <div>
      <h2>Inventory Management</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" value={form.name} onChange={handleChange} placeholder="Name" required />
        <input name="description" value={form.description} onChange={handleChange} placeholder="Description" required />
        <input name="category" value={form.category} onChange={handleChange} placeholder="Category" required />
        <input name="price" type="number" value={form.price} onChange={handleChange} placeholder="Price" required />
        <input name="quantity" type="number" value={form.quantity} onChange={handleChange} placeholder="Initial Quantity" required />
        <button type="submit">{editId ? 'Update' : 'Add'} Product</button>
      </form>
      <table>
        <thead>
          <tr><th>Name</th><th>Description</th><th>Category</th><th>Price</th><th>Quantity</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id} className={p.quantity < 10 ? 'low-stock' : ''}>
              <td>{p.name}</td>
              <td>{p.description}</td>
              <td>{p.category}</td>
              <td>{p.price}</td>
              <td>{p.quantity}</td>
              <td>
                <button onClick={() => handleEdit(p)}>Edit</button>
                <button onClick={() => handleDelete(p.id)}>Delete</button>
                <input type="number" value={addQty[p.id] || ''} onChange={(e) => handleQtyChange(p.id, e.target.value)} placeholder="Add Qty" />
                <button onClick={() => handleAddStock(p.id)}>Add Stock</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Inventory;
